$(function(){$("#getData").click(function(){var n=$("#txtName").val();$.get("/Home/Welcome",{input:n},function(n){if(n=="null"){$("#response").html("Please complete the form :)");return}var t="<table><tr><th>Name<\/th><th>Address<\/th><\/tr>";$.each(n,function(n,i){t+="<tr><td>"+i.Name+"<\/td><td>"+i.Address+"<\/td><\/tr>"});t+="<\/table>";$("#response").html(t)})})});